import 'package:category/feature/auth/model/data_auth/data_auth.dart';
import 'package:flutter_bloc/flutter_bloc.dart';

import 'auth_state.dart';

class AuthCubit extends Cubit<AuthState> {
  AuthCubit() : super(AuthInitial());

  final AuthData authData = AuthData();

  postDataCubit(
      {required namedata,
      required emaildata,
      required genderdata,
      required nationalIddata,
      required passworddata,
      required phonedata,
      required profileImagedata,
      required tokendata}) async {
    emit(AuthLoadingState());
    var user = await authData.postData(
        name: namedata,
        email: emaildata,
        gender: genderdata,
        nationalId: nationalIddata,
        password: passworddata,
        phone: phonedata,
        profileImage:
            "data:image/png;base64,",
        token: tokendata);

    emit(AuthSucessState(userdata: user));
  }
}
